    string getSeatFromSeatModel()
    {
        string htmlSeat = "";
        foreach (var i in seats)
        {
            if (seats.IndexOf(i) == 0 && seats.IndexOf(i) == seats.Count - 1)
            {
                htmlSeat += $"<span>{i}</span>";
            }
            else if (seats.IndexOf(i) == 0)
            {
                htmlSeat += $"<span>{i}, ";
            }
            else if (seats.IndexOf(i) == seats.Count - 1)
            {
                htmlSeat += $"{i}</span>";
            }
            else
            {
                htmlSeat += $"{i}, ";
            }
        }
        return htmlSeat;
    }

